import React, {useState, useEffect} from 'react';
import LoadingContext from '../context/LoadingContext';
import Loading from '../components/Loading';

const LoadingWrapper = ({children}) => {
    const [isLoading, setIsLoading] = useState(false);
    // console.log(isLoading, 'isLoading in loadingwrapper')

    useEffect(() => {
        console.log(isLoading, 'this is isLoading in useEffect iin laodingwarapper')
    }, [isLoading])

    const showLoadingSign = () => {
        if(isLoading === true){
          return (
              <Loading />
          )
        }
    }

    return (
        <>
         <LoadingContext.Provider value={{isLoading, setIsLoading, showLoadingSign}}>
            {children}
         </LoadingContext.Provider>
        </>
    )
}

export default LoadingWrapper;
