import React, {useEffect, useState} from 'react';
import Spacecraft from './Spacecraft';
import { useNavigate } from 'react-router-dom';
import SpaceTravelApi from '../services/SpaceTravelApi';
import SpacecraftList from '../components/SpacecraftList';


const Spacecrafts = () => {
    const [state, setState] = useState('');
    const navigate = useNavigate();
    const loadSpacecrafts = () => {
        // console.log(spacecrafts, 'spacecrafts')
        if(state !== ''){
            console.log(state, 'state')
                return (
                    state.forEach((spacecraft) => {
                    return <SpacecraftList pictureUrl={spacecraft.pictureUrl} name={spacecraft.name} capacity={spacecraft.capacity}/>
                })

                )
        }
    }

    useEffect(() => {
        const all = async () => {
            const responce = await SpaceTravelApi.getSpacecrafts();
            setState(responce.data);
        };

        all();
    }, [])

    return (
        <div>
            <button onClick={() => navigate('spacecraftbuild')}>üèóÔ∏èBuild a Spacecraft</button>
            <div>
              {loadSpacecrafts()}
            </div>
        </div>
    )
}

export default Spacecrafts;
