import React from 'react'; 
import DestinationContext from '../context/DestinationContext';
import { useContext } from 'react';

const FlyingSpaceship = ({name, capacity, pictureUrl, id, sendShipToPlanet}) => {
    // const {forceUpdate} = useContext(DestinationContext);

    const [, forceUpdate] = useReducer(x => x + 1, 0);

    const clicking = () => {
      console.log("clicking")
      sendShipToPlanet(id)
      forceUpdate();
    }

    return(
        <div onClick={() =>  clicking()}>
          <div className='img-container' onClick={forceUpdate}>
              <img src={pictureUrl} alt="spacecraft" className="img"/>
          </div>
          <p>{name}</p>
          <p>{capacity}</p>
        </div>
    )
}

export default FlyingSpaceship;
