import React, {useEffect, useState} from 'react';
import { useNavigate } from 'react-router-dom';
import SpaceTravelApi from '../services/SpaceTravelApi';
import Planet from '../components/ Planet';
import DestinationContext from '../context/DestinationContext';
import { useContext } from 'react';
import { useReducer } from 'react';

const Planets = () => {
    const navigate = useNavigate();
    const [planets, setPlanets] = useState('');
    // const {reRenderComponent, reRender} = useContext(DestinationContext);

    function useForceUpdate(){
      console.log("RUNNING")
      const [value, setValue] = useState(0); // integer state
      return () => setValue(value => value + 1); // update state to force render
      // A function that increment ðŸ‘†ðŸ» the previous state like here 
      // is better than directly setting `setValue(value + 1)`
  }
  const forceUpdate = useForceUpdate();

    useEffect(() => {
        const loadPlanets = async () => {
            const loadingAPI = await SpaceTravelApi.getPlanets();
            setPlanets(loadingAPI.data);
        }


        loadPlanets(); 
    }, [])

    const loadPlanetsFromEffect = () => {
        if(planets !== ''){
            return (
              planets.map((p) => {
                return (
                  <Planet 
                    id={p.id} 
                    key={p.id}
                    pictureUrl={p.pictureUrl} 
                    name={p.name} 
                    currentPopulation={p.currentPopulation}
                    onClick={forceUpdate}
                  />
                )
              })
            )
          }
    }

    // useEffect(() => {
    //   const update = () => {
    //     console.log("RUNNING")
    //     if(reRender === false){
    //       console.log("equal to false")
    //       reRenderComponent();
    //     }
    //   }

    //   update();
    // }, [])

    return (
        <>
           <button onClick={() => navigate(-1)}>Back</button>
           {loadPlanetsFromEffect()}
        </>
    )
}

export default Planets;
