import React, {useEffect, useState} from 'react';
import { useNavigate } from 'react-router-dom';
import SpaceTravelApi from '../services/SpaceTravelApi';
import Planet from '../components/ Planet';
import DestinationContext from '../context/DestinationContext';
import { useContext } from 'react';
import { useReducer } from 'react';

const Planets = () => {
    const navigate = useNavigate();
    const [planets, setPlanets] = useState('');
    const {forceUpdate, targetPlanet, targetSpacecraft, setTargetPlanet, setTargetSpacecraft, updateComponents} = useContext(DestinationContext);

    useEffect(() => {
        updateComponents();
        const loadPlanets = async () => {
            const loadingAPI = await SpaceTravelApi.getPlanets();
            setPlanets(loadingAPI.data);

        }


        loadPlanets(); 
    }, [])

    const loadPlanetsFromEffect = () => {
        if(planets !== ''){
            return (
              planets.map((p) => {
                return (
                  <Planet 
                    id={p.id} 
                    key={p.id}
                    pictureUrl={p.pictureUrl} 
                    name={p.name} 
                    currentPopulation={p.currentPopulation}
                  />
                )
              })
            )
          }
    }

  //   useEffect(() => {
  //     const sendShipToPlanet = async () => {
  //       console.log("ITS GOIN")
  //         if(targetSpacecraft !== null && targetPlanet !== null){
  //             SpaceTravelApi.sendSpacecraftToPlanet({'spacecraftId': targetSpacecraft, 'targetPlanetId': targetPlanet})
  //              setTargetPlanet(null);
  //              setTargetSpacecraft(null);
  //         }
  //     }

  //     sendShipToPlanet();
  // }, [])



    return (
        <div>
           <button onClick={() => navigate(-1)}>Back</button>
           {loadPlanetsFromEffect()}
        </div>
    )
}

export default Planets;
