import React, {useEffect, useState} from 'react';
import SpaceTravelApi from '../services/SpaceTravelApi';
import { useContext } from 'react';
import SpacecraftContext from '../context/SpacecraftContext';

const Spacecraft = () => {
    const [spacecraft, setSpacecraft] = useState('');
    const {currSpacecraft} = useContext(SpacecraftContext);

    const loadShipFromEffect = () => {
        if(spacecraft !== '' || null || undefined){
            for (let data in spacecraft){
                return (
                  <div className="container">
                  <div>
                    <img src={`${data.pictureUrl}`}/>
                  </div>
                  <p className="info">Name: {`${data.name}`}</p>
                  <p className="info">Capacity: {`${data.capacity}`}</p>
                  <p className="info">Description: {`${data.description}`}</p>
                  <button>Destroy</button>
                  </div>
                )
            }
        }
    }     

    useEffect(() => {
        const loadSpacecraft = async () => {
            const current = await SpaceTravelApi.getSpacecraftById({id: currSpacecraft});
            console.log(current.data, 'data')
            setSpacecraft(current.data);
        }

        loadSpacecraft();
    }, [])


    return (
       <>
        {loadShipFromEffect()}
       </>
    )
}

export default Spacecraft;
