import React, {useEffect, useState} from 'react';
import { useNavigate } from 'react-router-dom';
import SpaceTravelApi from '../services/SpaceTravelApi';
import Planet from '../components/ Planet';
import DestinationContext from '../context/DestinationContext';
import { useContext } from 'react';
import { useReducer } from 'react';

const Planets = () => {
    const navigate = useNavigate();
    const [planets, setPlanets] = useState('');
    const forceUpdate = useForceUpdate();
    const {targetPlanet, targetSpacecraft, setTargetPlanet, setTargetSpacecraft, updateComponents} = useContext(DestinationContext);

    function useForceUpdate() {
      const [state, setState] = useState(0);
      return () => setState((state) => state + 1);
    }

    useEffect(() => {
        const loadPlanets = async () => {
            const loadingAPI = await SpaceTravelApi.getPlanets();
            setPlanets(loadingAPI.data);

        }


        loadPlanets(); 
    }, [])

    const loadPlanetsFromEffect = () => {
      if(targetPlanet !== null){
        forceUpdate();
      }
        if(planets !== ''){
            return (
              planets.map((p) => {
                return (
                  <Planet 
                    id={p.id} 
                    key={p.id}
                    pictureUrl={p.pictureUrl} 
                    name={p.name} 
                    currentPopulation={p.currentPopulation}
                  />
                )
              })
            )
          }
    }

    return (
        <div onClick={() => forceUpdate()}>
           <button onClick={() => navigate(-1)}>Back</button>
           {loadPlanetsFromEffect()}
        </div>
    )
}

export default Planets;
