import React, {useState} from 'react';
import { useNavigate } from 'react-router-dom';
import { useContext } from 'react';
import SpacecraftContext from './context/SpacecraftContext';

const SpacecraftBuild = () => {
    const navigate = useNavigate();
    const {buildSpacecraft} = useContext(SpacecraftContext);
    const INITIAL_STATE = {
        spacecraftName: '',
        capacity: '',
        descritption: '',
        pictureUrl: ''
    }
    const [formData, setFormData] = useState(INITIAL_STATE);
    const handleChange = (e) => {
        const {name, value} = e.target; 
        setFormData(data => ({
            ...data,
            [name] : value
        }));
    }
    const handleClick = (e) => {
        e.preventDefault(); 
        buildSpacecraft({...formData})
        setFormData(INITIAL_STATE);
        alert(`making ship!`)
    }
    return (
        <>
            <button onClick={() => navigate(-1)}>Back</button>
            <form onSubmit={handleClick}>
                <input 
                  type="text" 
                  name="spacecraftName"
                  placeholder="Name" 
                  value={formData.spacecraftName} 
                  onChange={handleChange}
                />
                 <input 
                  type="text" 
                  name="capacity"
                  placeholder="Capacity" 
                  value={formData.capacity} 
                  onChange={handleChange}
                />
                <input 
                  type="text" 
                  name="descritption"
                  placeholder="Description" 
                  value={formData.descritption} 
                  onChange={handleChange}
                />
                <input 
                  type="text" 
                  name="pictureUrl"
                  placeholder="Picture URL" 
                  value={formData.pictureUrl} 
                  onChange={handleChange}
                />
                <button>üèóÔ∏èBuild</button>
            </form>
        </>
    )
}

export default SpacecraftBuild;
