import React from 'react';
import '../css/SpacecraftsList.css';
import { useContext } from 'react';
import SpacecraftProvider from '../providers/SpacecraftProvider';
import { useNavigate } from 'react-router-dom';

const SpacecraftList = ({pictureUrl, name, capacity, key}) => {
    const {setCurrSpacecraft} = useContext(SpacecraftProvider);
    const navigate = useNavigate();
    const showSpaceshipDetails = () => {
        setCurrSpacecraft(key);
        navigate('spacecraft');
    }
    return (
        <div className="container" onClick={showSpaceshipDetails}>
          <div>
            <img src={`${pictureUrl}`}/>
          </div>
          <p className="info">Name: {`${name}`}</p>
          <p className="info">Capacity: {`${capacity}`}</p>
          <button>Destroy</button>
        </div>
    )
}

export default SpacecraftList;
