import React, {useEffect, useState} from 'react';
import SpaceTravelApi from '../services/SpaceTravelApi';
import { useContext } from 'react';
import SpacecraftContext from '../context/SpacecraftContext';

const Spacecraft = () => {
    const [craft, setCraft] = useState('');
    const {currSpacecraft} = useContext(SpacecraftContext);



    useEffect(() => {
        const loadSpacecraft = async () => {
            const current = await SpaceTravelApi.getSpacecraftById({id: currSpacecraft});
            setCraft(current.data);
            // loadShipFromEffect(current.data);
            console.log(current.data, 'current')
        }

        loadSpacecraft();
    }, [])

   const loadShipFromEffect = () => {
    if(craft !== '' || null){
        console.log(craft, 'craft')
        return (
            craft.map((c) => {
                console.log(c, 'cccccccccccccccccccccccc')
                return (
                    <div className="container">
                    <div>
                      <img src={`${c.pictureUrl}`}/>
                    </div>
                    <p className="info">Name: {`${c.name}`}</p>
                    <p className="info">Capacity: {`${c.capacity}`}</p>
                    <p className="info">Description: {`${c.description}`}</p>
                    <button>Destroy</button>
                  </div>
                )
            }) 
        )
    }     
}
    return (
       <>
        {loadShipFromEffect()}
       </>
    )
}

export default Spacecraft;
