import React, {useEffect, useState} from 'react';
import { useNavigate } from 'react-router-dom';
import SpaceTravelApi from '../services/SpaceTravelApi';
import Planet from '../components/ Planet';
import DestinationContext from '../context/DestinationContext';
import { useContext } from 'react';
import { useReducer } from 'react';

const Planets = () => {
    const navigate = useNavigate();
    const [planets, setPlanets] = useState('');
    // const {reRenderComponent, reRender} = useContext(DestinationContext);
    const [, forceUpdate] = useReducer(x => x + 1, 0);

    useEffect(() => {
        const loadPlanets = async () => {
            const loadingAPI = await SpaceTravelApi.getPlanets();
            setPlanets(loadingAPI.data);
        }


        loadPlanets(); 
    }, [])

    const loadPlanetsFromEffect = () => {
        if(planets !== ''){
            return (
              planets.map((p) => {
                return (
                  <Planet 
                    id={p.id} 
                    key={p.id}
                    pictureUrl={p.pictureUrl} 
                    name={p.name} 
                    currentPopulation={p.currentPopulation}
                    onClick={forceUpdate}
                  />
                )
              })
            )
          }
    }

    // useEffect(() => {
    //   const update = () => {
    //     console.log("RUNNING")
    //     if(reRender === false){
    //       console.log("equal to false")
    //       reRenderComponent();
    //     }
    //   }

    //   update();
    // }, [])

    return (
        <>
           <button onClick={() => navigate(-1)}>Back</button>
           {loadPlanetsFromEffect()}
        </>
    )
}

export default Planets;
