import React, {useEffect} from 'react';
import SpaceTravelApi from '../services/SpaceTravelApi';
import { useContext } from 'react';
import SpacecraftContext from '../context/SpacecraftContext';

const Spacecraft = () => {
    const {currSpacecraft} = useContext(SpacecraftContext);
    console.log(currSpacecraft, 'currSpacecraft')

    const loadShipFromEffect = (current) => {
        return (
            <div className="container">
            <div>
              <img src={`${current.pictureUrl}`}/>
            </div>
            <p className="info">Name: {`${current.name}`}</p>
            <p className="info">Capacity: {`${current.capacity}`}</p>
            <p className="info">Description: {`${current.description}`}</p>
            <button>Destroy</button>
          </div>
        )
    }


    useEffect(() => {
        const loadSpacecraft = async () => {
            const current = await SpaceTravelApi.getSpacecraftById({id: currSpacecraft});
            loadShipFromEffect(current);
            console.log(current.data, 'current')
        }

        loadSpacecraft();
    }, [])
    return (
       <>
        {loadShipFromEffect()}
       </>
    )
}

export default Spacecraft;
