import React, {useEffect} from 'react';
import Spacecraft from './Spacecraft';
import { useNavigate } from 'react-router-dom';
import SpaceTravelApi from '../services/SpaceTravelApi';
import '../css/Spacecrafts.css';

const Spacecrafts = () => {
    const navigate = useNavigate();
    const loadSpacecrafts = (spacecrafts) => {
        console.log(spacecrafts, 'spacecrafts')
        if(spacecrafts !== undefined){
                {spacecrafts.forEach((spacecraft) => {
                    return (
                    <div className="container">
                        <div>
                          <img src={`${spacecraft.pictureUrl}`}/>
                        </div>
                        <p>Name: {`${spacecraft.name}`}</p>
                        <p>Capacity: {`${spacecraft.capacity}`}</p>
                    </div>
                    )


                })} 
        }
    }

    useEffect(() => {
        const all = async () => {
            const responce = await SpaceTravelApi.getSpacecrafts();
            loadSpacecrafts(responce.data);
        };

        all();
    }, [])

    return (
        <div>
            <button onClick={() => navigate('spacecraftbuild')}>üèóÔ∏èBuild a Spacecraft</button>
            {loadSpacecrafts()}
        </div>
    )
}

export default Spacecrafts;
