import React, {useEffect, useState} from 'react';
import SpaceTravelApi from '../services/SpaceTravelApi';
import { useContext } from 'react';
import SpacecraftContext from '../context/SpacecraftContext';

const Spacecraft = () => {
    const [spacecraft, setSpacecraft] = useState('');
    const {currSpacecraft} = useContext(SpacecraftContext);

    const loadShipFromEffect = (craft) => {
        if(craft !== '' && null){
            // console.log(craft, 'craft')
            return (
                spacecraft.map(() => {
                    return (
                        <div className="container">
                        <div>
                          <img src={`${craft.pictureUrl}`}/>
                        </div>
                        <p className="info">Name: {`${craft.name}`}</p>
                        <p className="info">Capacity: {`${craft.capacity}`}</p>
                        <p className="info">Description: {`${craft.description}`}</p>
                        <button>Destroy</button>
                      </div>
                    )
                }) 
            )
        }     
    }

    useEffect(() => {
        const loadSpacecraft = async () => {
            const current = await SpaceTravelApi.getSpacecraftById({id: currSpacecraft});
            // setCraft(current.data);
            // console.log(craft, 'craft')
            loadShipFromEffect(current.data);
            console.log(current.data, 'current')
        }

        loadSpacecraft();
    }, [loadShipFromEffect])


    return (
       <>
        {loadShipFromEffect()}
       </>
    )
}

export default Spacecraft;
