import React, {useEffect} from 'react';
import SpaceTravelApi from '../services/SpaceTravelApi';
import { useContext } from 'react';
import SpacecraftProvider from '../providers/SpacecraftProvider';

const ClickedSpacecraft = ({name, id, capacity, description, pictureUrl}) => {
  const {destroySpacecraft} = useContext(SpacecraftProvider);

    return (
        <>
        <div className="container" key={id}>
                    <div>
                      <img src={`${pictureUrl}`}/>
                    </div>
                    <p className="info">Name: {`${name}`}</p>
                    <p className="info">Capacity: {`${capacity}`}</p>
                    <p className="info">Description: {`${description}`}</p>
                    <button onClick={() => destroySpacecraft(id)}>Destroy</button>
                  </div>
        </>
    )
}

export default ClickedSpacecraft;
