import React, {useState, useEffect} from 'react';
import SpaceTravelApi from '../services/SpaceTravelApi';
import FlyingSpaceship from './FlyingSpaceship';
import DestinationContext from '../context/DestinationContext';
import { useContext } from 'react';
import Loading from '../components/Loading';
import LoadingContext from '../context/LoadingContext';
import '../css/Planet.css';

const Planet = ({ name, pictureUrl, currentPopulation, id, tellPlanetsToShowLoading}) => {
    const [data, setData] = useState([]);
    const {setTargetPlanet, setTargetSpacecraft, targetPlanet, targetSpacecraft} = useContext(DestinationContext);
    const {isLoading, setIsLoading} = useContext(LoadingContext);

    useEffect(() => {
        const findLandedSpacecrafts = async () => {
           const spacecrafts = await SpaceTravelApi.getSpacecrafts();
           const spacecraftsData = spacecrafts.data; 
           setData(spacecraftsData);
        }

        findLandedSpacecrafts();
    }, [])

    const sendShipToPlanet = (spacecraftId) => {
      setTargetSpacecraft(spacecraftId);
    }

    const addPlanetToDesination = () => {
        setTargetPlanet(id);
        // tellPlanetsToShowLoading();
    }


    // useEffect(() => {
    //     const sendShipToPlanet = async () => {
    //         if(targetSpacecraft !== null && targetPlanet !== null){
    //             console.log("running")
    //             setIsLoading(true);
    //             SpaceTravelApi.sendSpacecraftToPlanet({'spacecraftId': targetSpacecraft, 'targetPlanetId': targetPlanet})
    //             setTargetPlanet(null);
    //             setTargetSpacecraft(null);
    //             tellPlanetsToShowLoading();
    //         }
    //     }

    //     sendShipToPlanet();
    // }, [targetPlanet, targetSpacecraft])

    return (
        <>
            <div className="container">
                <div className="img-container" key={id}>
                    <img  onClick={() => addPlanetToDesination()} src={pictureUrl} className="img" alt="planet"/>
                    <p>{name}</p>
                <p>{currentPopulation}</p>
                </div>
                <div className="spaceship-container">
                {
                  data.filter((item) => item.currentLocation === id).map((s) => 
                    (
                        <FlyingSpaceship 
                          id={s.id} 
                          pictureUrl={s.pictureUrl} 
                          key={s.id} 
                          name={s.name} 
                          capcity={s.capacity}
                          sendShipToPlanet={sendShipToPlanet}
                        />
                    ))
                }
                </div>
            </div>
        </>
    )
}

export default Planet;
